import React, { useState } from 'react';
import CalculatePoints from './CalculatePoints';
import ShowAdditionalQues from './ShowAdditionalQues';
import ClearOptions2 from './ClearOptions2';

function StudentExercise2() {
  const [showOptions2, setShowOptions2] = useState(false);
  const [totalPoints, setTotalPoints] = useState(0);

  const handleShowAdditionalQues = () => {
    setShowOptions2(true);
  };

  const handleCalculatePoints = () => {
    CalculatePoints(setTotalPoints);
  };

  const handleClearOptions = () => {
    setShowOptions2(false);
    setTotalPoints(0);
    ClearOptions2();
  };

  return (
    <div>
      <h1>Student Exercise 2 [13 minutes]</h1>


      {showOptions2 && (
        <div id="options2">
          <p>Are the desired physics packaged GPU-enabled?</p>
          <label>
            <input type="radio" name="answer2" value="yes" />
            Yes [1 point]
          </label>
          <label>
            <input type="radio" name="answer2" value="no" />
            No [3 points]
          </label>
        </div>
      )}

      <p>Is the code written in such a way that it is GPU-ready?</p>
      <ul>
        <li>Is full parallelism available at the loop level?</li>
        <ul>
          <label>
            <input type="radio" name="answer3" value="yes" /> Yes [1 point]
          </label>
          <label>
            <input type="radio" name="answer3" value="no" /> No [7 points]
          </label>
        </ul>
        <li>Does a threaded version of the code exist?</li>
        <ul>
          <label>
            <input type="radio" name="answer4" value="yes" /> Yes [1 point]
          </label>
          <label>
            <input type="radio" name="answer4" value="no" /> No [7 points]
          </label>
        </ul>
        <li>Does the code have some form of verification?</li>
        <ul>
          <label>
            <input type="radio" name="answer5" value="yes" /> Yes [1 point]
          </label>
          <label>
            <input type="radio" name="answer5" value="no" /> No [7 points]
          </label>
        </ul>
      </ul>

      <button onClick={handleCalculatePoints}>Submit</button>
      <button onClick={handleClearOptions}>Clear</button>

      <p id="TotalPoints">Total Points: {totalPoints}</p>
      <p id="result"></p>
    </div>
  );
}

export default StudentExercise2;
