import React, { useState, useEffect } from 'react';
import CustomTooltip from './ToolTip';
import Button from './Button';
import Summary from './summary';


const StudentExercise0 = () => {
  const [answer, setAnswer] = useState('');
  const [answer2, setAnswer2] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [resultMessage, setResultMessage] = useState('');

  const [inputOne, setInputOne] = useState('');
  const [inputTwo, setInputTwo] = useState('');
  const [divisionResult, setDivisionResult] = useState('');
  const [showAnswers, setShowAnswers] = useState(false);


  const handleCalculation = () => {
    const x = parseFloat(inputOne);
    const y = parseFloat(inputTwo);

    if (!isNaN(x) && !isNaN(y)) {
      const division = x / y;
      setDivisionResult(division);
      localStorage.setItem('divisionResult', division);
    }
};

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'answer') {
      setAnswer(value);
      localStorage.setItem('IOfrequency', value);
    } else if (name === 'answer2') {
      setAnswer2(value);
      localStorage.setItem('RateThroughput', value);
    }
  };


  const handleSubmit = (e) => {
    e.preventDefault();

    if (answer && answer2) {
      setSubmitted(true);
      if (
        divisionResult <= 50 &&
        answer === 'yes' &&
        answer2 === 'rate'
      ) {
        setResultMessage(
          'Your application is a good match for GPU enabled computing! :)'
        );
      } else {
        setResultMessage(
          'Your application is a poor match for GPU enabled computing :('
        );
      }
      setShowAnswers(true);
    } else {
      alert('Please answer all questions.');
    }
  };



  const clearOptions = (e) => {
    e.preventDefault();
    setAnswer('');
    setAnswer2('');
    setDivisionResult("");
    setResultMessage ("");
    setInputOne("")
    setInputTwo("")
    setShowAnswers(false);
  };



  return (
    <div>
      <div>
      <ol>
        <li>Determine the following:</li>
        <p>Do you have a Makefile created for your code?</p>
        <br />
        <label>
          <input
            type="radio"
            name="answer"
            value="yes"
            checked={answer === 'yes'}
            onChange={(e) => setAnswer(e.target.value)}
          />{' '}
          Yes 
        </label>
        <label>
          <input
            type="radio"
            name="answer"
            value="no"
            checked={answer === 'no'}
            onChange={(e) => setAnswer(e.target.value)}
          />{' '}
          No
        </label>
        <br />
        <br />
       
        <Button text = "Calculate" onClick={handleCalculation} style={{ marginLeft: '10px' }} />
        <p>Number of grid-points per &#123;node, GPU&#125;: {divisionResult}</p>
      </ol>
    </div>
      <form onSubmit={handleSubmit}>
        <div>
          <ol start={2}>
            <li>Does it perform I/O frequently?</li><br></br>
            <div id="options">
              <label>
                <input
                  type="radio"
                  name="answer"
                  value="yes"
                  checked={answer === 'yes'}
                  onChange={handleInputChange}
                />{' '}
                Yes
              </label>
              <label>
                <input
                  type="radio"
                  name="answer"
                  value="no"
                  checked={answer === 'no'}
                  onChange={handleInputChange}
                />{' '}
                No
              </label>
            </div><br></br>
            <li><div style={{ display: 'flex', alignItems: 'center' }}>
                Do you have rate or throughput limitations?
                <CustomTooltip />
              </div></li> 
            <br></br>
            <div id="options2">
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="rate"
                  checked={answer2 === 'rate'}
                  onChange={handleInputChange}
                />{' '}
                Rate
              </label>
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="throughput"
                  checked={answer2 === 'throughput'}
                  onChange={handleInputChange}
                />{' '}
                Throughput
              </label> 
              <br></br>
            </div>
          </ol>
        </div><br></br>

       
        <Button text="Submit"/>
        <Button text = "Clear" onClick = {clearOptions} />
       
        {/*<button type="button" style={{ marginLeft: '100px' }} onClick={clearOptions}>
          Clear
  </button>*/}
      </form><br></br>
      {showAnswers && (
        <div style={{ marginLeft: '40px' }}>
          <div id="ans">
            Your Answers:
            <br />
            
            <p>{divisionResult},{answer}, {answer2}</p>
          </div>
         
          <div id="result2">{resultMessage}</div><br></br>
        </div>
      )}
    </div>
  );
};

export default StudentExercise0;
