import React from 'react';
import CalcOne from './CalcOne';

class Submit extends React.Component {
  state = {
    divisionResult: null, // Initialize divisionResult in Submit component's state
  };

  handleCalculation = (division) => {
    this.setState({ divisionResult: division }); // Update divisionResult in Submit component's state
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const answer = document.querySelector('input[name="answer"]:checked');
    const answer2 = document.querySelector('input[name="answer2"]:checked');
    const divisionResult = this.state.divisionResult;

    if (answer && answer2) {
      // this line of code displays the answers chosen by the user
      document.getElementById("ans").innerHTML =
        "Your Answers: " +
        divisionResult +
        ", " +
        answer.value +
        " and " +
        answer2.value;
    } else {
      // this line of code alerts the user if all the answers are not selected
      alert("Please answer all questions.");
    }

    const result2Element = document.getElementById("result2");

    const radios1 = document.getElementsByName("answer");
    const radios2 = document.getElementsByName("answer2");

    // if the division result is less than 50 AND Yes is checked in 2nd question AND Rate is checked in 3rd question,
    // the following message is displayed:
    if (
      divisionResult <= 50 &&
      radios1[0].checked &&
      radios2[0].checked
    ) {
      result2Element.innerHTML =
        "Your application is a good match for GPU-enabled computing!";
    }
    // if the division result is more than 50 AND No is checked in 2nd question AND Throughput is checked in 3rd question,
    // the following message is displayed:
    else if (
      divisionResult > 50 &&
      radios1[1].checked &&
      radios2[1].checked
    ) {
      result2Element.innerHTML =
        "Your application is a poor match for GPU-enabled computing :(";
    } else {
      // In all other combinations of scenarios, the following message is displayed:
      result2Element.innerHTML =
        "Your application is a poor match for GPU-enabled computing :(";
    }
  };

  render() {
    const { divisionResult } = this.state;
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div>
            <CalcOne onDivisionResultChange={this.handleCalculation} /> {/* Pass the handleCalculation function as a prop */}
            <ol start={2}>
              <li>Does it perform I/O frequently? </li>
              {/* Radio options for Question 1*/}
              <div id="options">
                <label>
                  <input type="radio" name="answer" value="yes" /> Yes
                </label>
                <label>
                  <input type="radio" name="answer" value="no" /> No
                </label>
              </div>
              <li>Do you have rate or throughput limitations?</li>
              {/* Radio options for Question 2*/}
              <div id="options2">
                <label>
                  <input type="radio" name="answer2" value="rate" /> Rate
                </label>
                <label>
                  <input type="radio" name="answer2" value="throughput" /> Throughput
                </label>
              </div>
            </ol>
          </div>

          <button type="submit">Submit</button>
        </form>
        <div id="ans"></div>
        <div id="result2"></div>
      </div>
    );
  }
}

export default Submit;
