import React from 'react';
import Box from '@mui/material/Box';

const CustomBox = ({ bgcolor, color, marginTop, marginBottom, children }) => {
  const boxStyles = {
    width: "400px",
    backgroundColor: bgcolor || '#36545d',
    color: color || 'white',
    marginTop: marginTop || 10,
    marginBottom: marginBottom || 3,
    marginLeft: "120px",
    borderRadius: '30px',
    overflowY: 'auto', 
    padding: '35px',
    fontSize: '18px',
  };

  const contentStyles = {
    maxHeight: "calc(100vh - 200px)", // Adjust the value as needed
    overflowY: 'auto',
  };

  return (
    
    <div style={containerStyles}>
      <Box sx={contentStyles}>
        {children}
      </Box>
    </div>
  );
};

export default CustomBox;
