import React, { useState } from 'react';

function StudentExercise2() {
  const [showOptions2, setShowOptions2] = useState(false);
  const [totalPoints, setTotalPoints] = useState(0);

  const handleShowAdditionalQues = (event) => {
    const selectedValue = event.target.value;
    setShowOptions2(selectedValue === 'yes');
  };

  const calculatePoints = () => {
    const radios1 = document.getElementsByName('answer');
    const radios2 = document.getElementsByName('answer2');
    const radios3 = document.getElementsByName('answer3');
    const radios4 = document.getElementsByName('answer4');
    const radios5 = document.getElementsByName('answer5');
  
    let newTotalPoints = 0;
    let allOptionsSelected = true; // Flag to track if all options are selected
  
    if (
      (radios1[0].checked || radios1[1].checked) &&
      ((radios1[0].checked && (radios2[0].checked || radios2[1].checked)) ||
        radios1[1].checked) &&
      (radios3[0].checked || radios3[1].checked) &&
      (radios4[0].checked || radios4[1].checked) &&
      (radios5[0].checked || radios5[1].checked)
    ) {
      if (radios1[0].checked) {
        newTotalPoints += 0;
  
        if (radios2[0].checked) {
          newTotalPoints += 1;
        } else if (radios2[1].checked) {
          newTotalPoints += 3;
        }
      } else if (radios1[1].checked) {
        newTotalPoints += 4;
      }
  
      if (radios3[0].checked) {
        newTotalPoints += 1;
      } else if (radios3[1].checked) {
        newTotalPoints += 7;
      }
  
      if (radios4[0].checked) {
        newTotalPoints += 1;
      } else if (radios4[1].checked) {
        newTotalPoints += 7;
      }
  
      if (radios5[0].checked) {
        newTotalPoints += 1;
      } else if (radios5[1].checked) {
        newTotalPoints += 7;
      }
    } else {
      allOptionsSelected = false; // Set the flag to false if any option is not selected
    }
  
    setTotalPoints(newTotalPoints);
  
    if (!allOptionsSelected) {
      alert('Please select all options before calculating points.');
    }
  };
  

  return (
    <div style={{ marginLeft: '20px' }}>
      <ol>
        <div id="options">
          <li>Does a GPU version of your code already exist?</li>
          <br />
          <label>
            <input
              type="radio"
              name="answer"
              value="yes"
              onClick={handleShowAdditionalQues}
            />{' '}
            Yes [0 points]
          </label>
          <label>
            <input
              type="radio"
              name="answer"
              value="no"
              onClick={handleShowAdditionalQues}
            />{' '}
            No [4 points]
          </label>
        </div>
        <br />

        {showOptions2 && (
          <div id="options2">
            <ul>
              <li>Are the desired physics packaged GPU-enabled?</li>
              <br />
              <label>
                <input type="radio" name="answer2" value="yes" /> Yes [1 point]
              </label>
              <label>
                <input type="radio" name="answer2" value="no" /> No [3 points]
              </label>
            </ul>
          </div>
        )}
        <br />

        <li>Is the code written in such a way that it is GPU-ready?</li>
        <br />
        <ul>
          <li>Is full parallelism available at the loop level?</li>
          <br />
          <ul>
            <label>
              <input type="radio" name="answer3" value="yes" /> Yes [1 point]
            </label>
            <label>
              <input type="radio" name="answer3" value="no" /> No [7 points]
            </label>
          </ul>
          <br />
          <li>Does a threaded version of the code exist?</li>
          <br />
          <ul>
            <label>
              <input type="radio" name="answer4" value="yes" /> Yes [1 point]
            </label>
            <label>
              <input type="radio" name="answer4" value="no" /> No [7 points]
            </label>
          </ul>
          <br />
          <li>Does the code have some form of verification?</li>
          <br />
          <ul>
            <label>
              <input type="radio" name="answer5" value="yes" /> Yes [1 point]
            </label>
            <label>
              <input type="radio" name="answer5" value="no" /> No [7 points]
            </label>
            <br />
          </ul>
        </ul>
        <br />
        <br />

        <button onClick={handleCalculatePoints}>Submit</button>
        <button onClick={handleClearOptions} style={{ marginLeft: '40px' }}>
          Clear
        </button>
        <br />
        <br />
        <p id="TotalPoints">Total Points: {totalPoints}</p>
        <br />
        {/*<p id="result">{totalPoints > 0 && resultMessage}</p>*/}
      </ol>
    </div>
  );
}

export default StudentExercise2
