import React, { useState } from 'react';

function StudentExercise2() {
  const [showOptions2, setShowOptions2] = useState(false);
  const [totalPoints, setTotalPoints] = useState(0);
  const [answer, setAnswer] = useState('');
  const [answer2, setAnswer2] = useState('');
  const [answer3, setAnswer3] = useState('');
  const [answer4, setAnswer4] = useState('');
  const [answer5, setAnswer5] = useState('');



  const handleShowAdditionalQues = (event) => {
    const selectedValue = event.target.value;
    setShowOptions2(selectedValue === 'yes');
  };

  const calculatePoints = () => {
    let newTotalPoints = 0;
    let allOptionsSelected = true;

    if (
      (answer === 'yes' || answer === 'no') &&
      ((answer === 'yes' && (answer2 === 'yes' || answer2 === 'no')) ||
        answer === 'no') &&
      (answer3 === 'yes' || answer3 === 'no') &&
      (answer4 === 'yes' || answer4 === 'no') &&
      (answer5 === 'yes' || answer5 === 'no')
    ) {
      if (answer === 'yes') {
        newTotalPoints += 0;

        if (answer2 === 'yes') {
          newTotalPoints += 1;
        } else if (answer2 === 'no') {
          newTotalPoints += 3;
        }
      } else if (answer === 'no') {
        newTotalPoints += 4;
      }

      if (answer3 === 'yes') {
        newTotalPoints += 1;
      } else if (answer3 === 'no') {
        newTotalPoints += 7;
      }

      if (answer4 === 'yes') {
        newTotalPoints += 1;
      } else if (answer4 === 'no') {
        newTotalPoints += 7;
      }

      if (answer5 === 'yes') {
        newTotalPoints += 1;
      } else if (answer5 === 'no') {
        newTotalPoints += 7;
      }
    } else {
      allOptionsSelected = false;
    }

    setTotalPoints(newTotalPoints);

    if (!allOptionsSelected) {
      alert('Please select all options before calculating points.');
    }
  };

  const handleCalculatePoints = () => {
    calculatePoints();
  };

  const handleClearOptions = () => {
    setShowOptions2(false);
    setTotalPoints(0);
    clearOptions();
  };

  const clearOptions = () => {
    setAnswer('');
    setAnswer2('');
    setAnswer3('');
    setAnswer4('');
    setAnswer5('');
    setTotalPoints(0);
  };

  return (
    <div style={{ marginLeft: '20px' }}>
      <ol>
        <div id="options">
          <li>Does a GPU version of your code already exist?</li>
          <br />
          <label>
            <input
              type="radio"
              name="answer"
              value="yes"
              onChange={(e) => setAnswer(e.target.value)}
            />{' '}
            Yes [0 points]
          </label>
          <label>
            <input
              type="radio"
              name="answer"
              value="no"
              onChange={(e) => setAnswer(e.target.value)}
            />{' '}
            No [4 points]
          </label>
        </div>
        <br />

        {showOptions2 && (
          <div id="options2">
            <ul>
              <li>Are the desired physics packaged GPU-enabled?</li>
              <br />
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="yes"
                  onChange={(e) => setAnswer2(e.target.value)}
                />{' '}
                Yes [1 point]
              </label>
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="no"
                  onChange={(e) => setAnswer2(e.target.value)}
                />{' '}
                No [3 points]
              </label>
            </ul>
          </div>
        )}
        <br />

        <li>Is the code written in such a way that it is GPU-ready?</li>
        <br />
        <ul>
          <li>Is full parallelism available at the loop level?</li>
          <br />
          <ul>
            <label>
              <input
                type="radio"
                name="answer3"
                value="yes"
                onChange={(e) => setAnswer3(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer3"
                value="no"
                onChange={(e) => setAnswer3(e.target.value)}
              />{' '}
              No [7 points]
            </label>
          </ul>
          <br />
          <li>Does a threaded version of the code exist?</li>
          <br />
          <ul>
            <label>
              <input
                type="radio"
                name="answer4"
                value="yes"
                onChange={(e) => setAnswer4(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer4"
                value="no"
                onChange={(e) => setAnswer4(e.target.value)}
              />{' '}
              No [7 points]
            </label>
          </ul>
          <br />
          <li>Does the code have some form of verification?</li>
          <br />
          <ul>
            <label>
              <input
                type="radio"
                name="answer5"
                value="yes"
                onChange={(e) => setAnswer5(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer5"
                value="no"
                onChange={(e) => setAnswer5(e.target.value)}
              />{' '}
              No [7 points]
            </label>
            <br />
          </ul>
        </ul>
        <br />
        <br />

        <button onClick={handleCalculatePoints}>Submit</button>
        <button onClick={handleClearOptions} style={{ marginLeft: '40px' }}>
          Clear
        </button>
        <br />
        <br />
        <p id="TotalPoints">Total Points: {totalPoints}</p>
        <br />
      </ol>
    </div>
  );
}

export default StudentExercise2;
