import React from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import ProgressBar from './ProgressBar';
import Hiking from './hiking';
import Header from './header';
import Page2 from './page2';
import StudentExercise1 from './StudentExercise1';
import Button from './Button';

const Page1 = () => {
  const completed = 0; // Set the initial completed value here
  const navigate = useNavigate();

  // Function to handle click event and navigate to '/page2'
  function handleClick(event) {
    navigate('/page2');
  }

  // Function to handle click event and navigate to '/'
  function handleOnClick2(event) {
    navigate('/');
  }

  return (
    <div className="container">
      <div className="background-image">
        <Hiking /> // Render the Hiking component
      </div>
      <div className="content">
        <Header title="STUDENT EXERCISE 1" subtitle="5 MINUTES" /> // Render the Header component with title and subtitle

        <ProgressBar bgcolor="#36545d" completed={completed} /> // Render the ProgressBar component with bgcolor and completed prop

        <Routes> // Start of nested routing
          <Route path="/*" element={<StudentExercise1 />} /> // Route for StudentExercise1 component
          <Route path="/page2" element={<Page2 />} /> // Route for Page2 component
        </Routes> // End of nested routing

        <button type="button" onClick={handleClick} style={{ marginLeft: '40px' }}>
          Next Page
        </button> // Render a button to navigate to '/page2' on click

        <Button text=" Back " onClick={handleOnClick2} style={{ padding: "10px 28px", marginTop: "20px" }} /> // Render a custom Button component with text and onClick event handler
      </div>
    </div>
  );
};

export default Page1;
