import React from 'react';

// Import the necessary dependencies, in this case, the React library.

const Button = ({ text, onClick, style }) => {
  // Declare a functional component called "Button" that accepts three props:
  // - text: the text to display on the button
  // - onClick: the event handler function for the button's click event
  // - style: an optional object containing additional CSS styles for the button

  const buttonStyle = {
    backgroundColor: '#36545d', /* Set your desired background color */
    color: 'white', /* Set the text color */
    border: 'none', /* Remove the button border */
    padding: '10px 20px', /* Adjust the padding as needed */
    fontSize: '16px', /* Set the font size */
    cursor: 'pointer', /* Add a cursor pointer on hover */
    borderRadius: '5px',
    marginLeft: "50px",
    ...style
  };
  
  // Define an object called "buttonStyle" that contains the default CSS styles for the button.
  // It includes properties such as background color, text color, border, padding, font size,
  // cursor type, border radius, and an optional object spread for additional styles passed via props.

  const hoverStyle = {
    backgroundColor: '#45a049', /* Change the background color on hover */
  };
  
  // Define an object called "hoverStyle" that contains the CSS styles to be applied when the button is hovered over.
  // It only includes the background color property to change the background color on hover.

  return (
    <button
      style={buttonStyle}
      onClick={onClick}
    >
      {text}
    </button>
  );
  
  // Return a button element with the following properties:
  // - style: apply the "buttonStyle" object as the inline style for the button
  // - onClick: set the provided click event handler function for the button
  // The text to display on the button is rendered as the content of the button element.
};

export default Button;
  
