import React, {useState} from "react";
import './App.css';
import ProgressBar from "./ProgressBar"
import "./StudentExercises"

const testData = [
  { bgcolor: "#00695c", completed: 30 },

];


function App() {
  const [currentPage, setCurrentPage] = useState(1);

  const handleNextPage = () => {
    if (currentPage < 3) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handleCompleteExercises = () => {
    if (currentPage < 3) {
      setCurrentPage(currentPage + 1);
    } else {
      alert('All exercises completed!');
    }
  };

  return (
    <div>
       {testData.map((item, idx) => (
        <ProgressBar key={idx} bgcolor={item.bgcolor} completed={item.completed} />
      ))}

      {/* Your page content */}
      {/* Include buttons for navigating to the next page and completing exercises */}
      <button onClick={handleNextPage}>Next Page</button>
      <button onClick={handleCompleteExercises}>Complete Exercises</button>
    </div>
  );
}

export default App;
