import React, { useState } from 'react';

export function CalculatePoints() {
  const [totalPoints, setTotalPoints] = useState(0);

  const handleCalculatePoints = () => {
    const radios1 = document.getElementsByName('answer');
    const radios2 = document.getElementsByName('answer2');
    const radios3 = document.getElementsByName('answer3');
    const radios4 = document.getElementsByName('answer4');
    const radios5 = document.getElementsByName('answer5');

    let newTotalPoints = 0;

    if (
      (radios1[0].checked || radios1[1].checked) &&
      ((radios1[0].checked && (radios2[0].checked || radios2[1].checked)) ||
        radios1[1].checked) &&
      (radios3[0].checked || radios3[1].checked) &&
      (radios4[0].checked || radios4[1].checked) &&
      (radios5[0].checked || radios5[1].checked)
    ) {
      if (radios1[0].checked) {
        newTotalPoints += 0;

        if (radios2[0].checked) {
          newTotalPoints += 1;
        } else if (radios2[1].checked) {
          newTotalPoints += 3;
        }
      } else if (radios1[1].checked) {
        newTotalPoints += 4;
      }

      if (radios3[0].checked) {
        newTotalPoints += 1;
      } else if (radios3[1].checked) {
        newTotalPoints += 7;
      }

      if (radios4[0].checked) {
        newTotalPoints += 1;
      } else if (radios4[1].checked) {
        newTotalPoints += 7;
      }

      if (radios5[0].checked) {
        newTotalPoints += 1;
      } else if (radios5[1].checked) {
        newTotalPoints += 7;
      }

      setTotalPoints(newTotalPoints);

      const totalPointsElement = document.getElementById('TotalPoints');
      if (totalPointsElement) {
        totalPointsElement.textContent = 'Total Points: ' + newTotalPoints;
      }

      const resultElement = document.getElementById('result');
      if (resultElement) {
        if (0 < newTotalPoints && newTotalPoints <= 10) {
          resultElement.innerHTML =
            'Easy Peezy! It is going to be very easy to make your application GPU enabled.';
        } else if (10 < newTotalPoints && newTotalPoints <= 15) {
          resultElement.innerHTML =
            'Not too hard! It is going to take some work to make your application GPU enabled.';
        } else if (15 < newTotalPoints && newTotalPoints <= 25) {
          resultElement.innerHTML =
            'Difficult! It is going to be very hard to make your application GPU enabled.';
        }
      }
    } else {
      alert('Please select all options before calculating points.');
    }
  };

  return (
    <div>
      {/* Your JSX code for the radio inputs goes here */}

      <button onClick={handleCalculatePoints}>Calculate Points</button>

      <p id="TotalPoints">Total Points: {totalPoints}</p>
      <p id="result"></p>
    </div>
  );
}
