import React, { useState, useEffect } from 'react';
import CustomTooltip from './ToolTip';
import Button from './Button';
import Summary from './summary';


const StudentExercise1 = () => {
  const [answer, setAnswer] = useState('');
  const [answer2, setAnswer2] = useState('');
  const [submitted, setSubmitted] = useState(false);
  const [resultMessage, setResultMessage] = useState('');

  const [inputOne, setInputOne] = useState('');
  const [inputTwo, setInputTwo] = useState('');
  const [inputThree, setInputThree] = useState('');
  const [inputFour, setInputFour] = useState('');
  const [divisionResult, setDivisionResult] = useState('');
 

  const handleCalculation = () => {
    const x = parseFloat(inputOne);
    const y = parseFloat(inputTwo);

    if (!isNaN(x) && !isNaN(y)) {
      const division = x / y;
      setDivisionResult(division);
      localStorage.setItem('divisionResult', division);
    }
};

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'answer') {
      setAnswer(value);
      localStorage.setItem('IOfrequency', value);
    } else if (name === 'answer2') {
      setAnswer2(value);
      localStorage.setItem('RateThroughput', value);
    }
  };



  const handleSubmit = (e) => {
    handleCalculation()
    
    if (answer && answer2 && divisionResult) {
      setSubmitted(true);
      if (
        divisionResult <= 50 &&
        answer === 'yes' &&
        answer2 === 'rate'
      ) {
        setResultMessage(
          'Your application is a good match for GPU enabled computing! :)'
        );
      } else {
        setResultMessage(
          'Your application is a poor match for GPU enabled computing :('
        );
      }
      
    } else {
      alert('Please answer all questions.');
    }
    e.preventDefault();
  };



  const clearOptions = (e) => {
    e.preventDefault();
    setAnswer('');
    setAnswer2('');
    setDivisionResult("");
    setResultMessage ("");
    setInputOne("")
    setInputTwo("")
    setInputThree("")
    setInputFour("")

  };



  return (
    <div>
      <div>
      <ol>
        <li>Determine the following:</li>
        
       
      </ol>
    </div>
      <form onSubmit={handleSubmit}>
        <div>
          
          <ol start={2}>
          <p>Total number of independent grid-points</p>
        <input
          type="text"
          value={inputOne}
          onChange={(e) => setInputOne(e.target.value)}
        />
        <br />
        <br />
        <p>Number of &#123;node, GPU&#125;: per run</p>
        <input
          type="text"
          value={inputTwo}
          onChange={(e) => setInputTwo(e.target.value)}
        />
        <br />
        <br />
            <li>What is the volume and frequency of I/O?</li><br></br>
            <div id="options">
              <label>
                <input
                  type="radio"
                  name="answer"
                  value="yes"
                  checked={answer === 'yes'}
                  onChange={handleInputChange}
                />{' '}
                Yes
              </label>
              <label>
                <input
                  type="radio"
                  name="answer"
                  value="no"
                  checked={answer === 'no'}
                  onChange={handleInputChange}
                />{' '}
                No
              </label>
            </div><br></br>
            <li><div style={{ display: 'flex', alignItems: 'center' }}>
                Do you have rate or throughput limitations?
                <CustomTooltip />
              </div></li> 
            <br></br>
            <div id="options2">
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="rate"
                  checked={answer2 === 'rate'}
                  onChange={handleInputChange}
                />{' '}
                Rate
              </label>
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="throughput"
                  checked={answer2 === 'throughput'}
                  onChange={handleInputChange}
                />{' '}
                Throughput
              </label> 
              <br></br>
            </div>
          </ol>
        </div><br></br>

       
        <Button text="Submit"/>
        <Button text = "Clear" onClick = {clearOptions} />
        </form><br></br>
        {/*<button type="button" style={{ marginLeft: '100px' }} onClick={clearOptions}>
          Clear
  </button>
     
      {showAnswers && (
        <div style={{ marginLeft: '40px' }}>
          <div id="ans">
            Your Answers:
            <br />
            
            <p>{divisionResult},{answer}, {answer2}</p>
          </div>
         
          <div id="result2">{resultMessage}</div><br></br>
      </div>*/}
      
    </div>
  );
};

export default StudentExercise1;
