import React, { useState } from "react";
import "./App.css";
import ProgressBar from "./ProgressBar";
import "./StudentExercises";

const testData = [
  { bgcolor: "#6a1b9a", completed: 60 },
  { bgcolor: "#00695c", completed: 30 },
  { bgcolor: "#ef6c00", completed: 53 },
];

function App() {
  const [progress, setProgress] = useState(0);

  const calculateTotalProgress = () => {
    let totalCompleted = 0;
    testData.forEach((item) => {
      totalCompleted += item.completed;
    });
    const averageProgress = totalCompleted / testData.length;
    setProgress(averageProgress);
  };

  return (
    <div>
      <ProgressBar bgcolor="#3f51b5" completed={progress} />

      {/* Your page content */}
      {/* Include buttons for navigating to the next page and completing exercises */}
    </div>
  );
}


export default App;
