import React, { useState } from 'react';

export const ShowAdditionalQues = () => {
  const [showOptions2, setShowOptions2] = useState(false);

  const handleRadioChange = () => {
    const radios1 = document.getElementsByName("answer");

    if (radios1[0].checked) {
      setShowOptions2(true);
    } else {
      setShowOptions2(false);
    }
  };

  return (
    <div>
      <input type="radio" name="answer" onChange={handleRadioChange} />
      {/* Other HTML elements here */}
      {showOptions2 && (
        <div id="options2">
          <p>Are the desired physics packaged GPU-enabled?</p>
          <label>
            <input type="radio" name="answer2" value="yes" /> Yes [1 point]
          </label>
          <label>
            <input type="radio" name="answer2" value="no" /> No [3 points]
          </label>
        </div>
      )}
    </div>
  );
};

export default ShowAdditionalQues;
