import React, { useState } from 'react';

const ShowAdditionalQues = () => {
  const [showOptions2, setShowOptions2] = useState(false);

  const handleRadioChange = (event) => {
    const { value } = event.target;
    setShowOptions2(value === 'yes');
  };

  const [showAdditionalQuestions, setShowAdditionalQuestions] = useState(false);

  const handleAdditionalQuestionsChange = (event) => {
    const { value } = event.target;
    setShowAdditionalQuestions(value === 'yes');
  };

  return (
    <div>
      <label>
        <input type="radio" name="answer" value="yes" onChange={handleRadioChange} />
        Yes [0 points]
      </label>
      <label>
        <input type="radio" name="answer" value="no" onChange={handleRadioChange} />
        No [4 points]
      </label>

      {showOptions2 && (
        <div id="options2">
          <p>Are the desired physics packaged GPU-enabled?</p>
          <label>
            <input type="radio" name="answer2" value="yes" />
            Yes [1 point]
          </label>
          <label>
            <input type="radio" name="answer2" value="no" />
            No [3 points]
          </label>
        </div>
      )}
    </div>
  );
};

export default ShowAdditionalQues;
