import React, { useState } from 'react';

const Submit = () => {
  const [answer, setAnswer] = useState('');
  const [answer2, setAnswer2] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const [inputOne, setInputOne] = useState('');
  const [inputTwo, setInputTwo] = useState('');
  const [divisionResult, setDivisionResult] = useState('');

  const handleCalculation = () => {
    const x = parseFloat(inputOne);
    const y = parseFloat(inputTwo);

    if (!isNaN(x) && !isNaN(y)) {
      const division = x / y;
      setDivisionResult(division);
    }
};

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name === 'answer') {
      setAnswer(value);
    } else if (name === 'answer2') {
      setAnswer2(value);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (answer && answer2) {
      setSubmitted(true);
    } else {
      alert('Please answer all questions.');
    }
  };

  let resultMessage = '';
  if (submitted) {
    if (answer === 'yes' && answer2 === 'rate') {
      resultMessage = 'Your application is a good match for GPU enabled computing!';
    } else {
      resultMessage = 'Your application is a poor match for GPU enabled computing :(';
    }
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <ol start={2}>
            <li>Does it perform I/O frequently?</li><br></br>
            <div id="options">
              <label>
                <input
                  type="radio"
                  name="answer"
                  value="yes"
                  checked={answer === 'yes'}
                  onChange={handleInputChange}
                />{' '}
                Yes
              </label>
              <label>
                <input
                  type="radio"
                  name="answer"
                  value="no"
                  checked={answer === 'no'}
                  onChange={handleInputChange}
                />{' '}
                No
              </label>
            </div><br></br>
            <li>Do you have rate or throughput limitations?</li>
            <br></br>
            <div id="options2">
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="rate"
                  checked={answer2 === 'rate'}
                  onChange={handleInputChange}
                />{' '}
                Rate
              </label>
              <label>
                <input
                  type="radio"
                  name="answer2"
                  value="throughput"
                  checked={answer2 === 'throughput'}
                  onChange={handleInputChange}
                />{' '}
                Throughput
              </label>
              <br></br>
            </div>
          </ol>
        </div><br></br>

        <button type="submit" style={{ marginLeft: '40px' }}>Submit</button> <br></br>
      </form><br></br>
      {submitted && (
        <div style={{ marginLeft: '40px' }}>
          <div id="ans">Your Answers: {answer}, {answer2}</div>
          <div id="result2">{resultMessage}</div>
        </div>
      )}
    </div>
  );
};

export default Submit;
