import React, { useState } from 'react';
import Button from './Button';

function StudentExercise2() {
  const [showOptions2, setShowOptions2] = useState(false);
  const [PointResult, setPointResult] = useState("")
  const [result, setResult] = useState('');
  const [answer, setAnswer] = useState('');
  const [answer2, setAnswer2] = useState('');
  const [answer3, setAnswer3] = useState('');
  const [answer4, setAnswer4] = useState('');
  const [answer5, setAnswer5] = useState('');

  const handleShowAdditionalQues = (event) => {
    const selectedValue = event.target.value;
    setShowOptions2(selectedValue === 'yes');
  };

  const calculatePoints = () => {
    let newTotalPoints = 0;
    let allOptionsSelected = true; // Flag to track if all options are selected

    if (
      answer &&
      ((answer === 'yes' &&
        (answer2 === 'yes' || answer2 === 'no')) ||
        answer === 'no') &&
      (answer3 === 'yes' || answer3 === 'no') &&
      (answer4 === 'yes' || answer4 === 'no') &&
      (answer5 === 'yes' || answer5 === 'no')
    ) {
      if (answer === 'yes') {
        newTotalPoints += 0;

        if (answer2 === 'yes') {
          newTotalPoints += 1;
        } else if (answer2 === 'no') {
          newTotalPoints += 3;
        }
      } else if (answer === 'no') {
        newTotalPoints += 4;
      }

      if (answer3 === 'yes') {
        newTotalPoints += 1;
      } else if (answer3 === 'no') {
        newTotalPoints += 7;
      }

      if (answer4 === 'yes') {
        newTotalPoints += 1;
      } else if (answer4 === 'no') {
        newTotalPoints += 7;
      }

      if (answer5 === 'yes') {
        newTotalPoints += 1;
      } else if (answer5 === 'no') {
        newTotalPoints += 7;
      }
    } else {
      allOptionsSelected = false; // Set the flag to false if any option is not selected
    }

    setPointResult("Total Points" , newTotalPoints)
    localStorage.setItem("TotalPoints", newTotalPoints);

    let reult = ""
    let num = 0
    const start = "Let’s now discuss the difficulty of achieving the ROI."
    reult += start

    const string1 = "Based on your answers, it appears that your code is ready to start the process of GPU-enablement.  Let us try and figure out how much work it might take."

    const ques1yes = "It is great news that a version of your application has already been GPU-enabled.  This considerably reduces the difficulty." 

    const ques1no = "It looks like your science objective is not currently GPU-enabled. Based on your answers we can estimate effort to achieve GPU-enablement."

    const ques1Ayes = "Also great news that the necessary physics packages are supported on GPU.  It sounds like you just need to learn how to submit jobs to a GPU-based platform"

    const ques1Bno = "Unfortunately  not all of the code necessary for your science objective is GPU-enabled.  The amount of work necessary to GPU-enable your science objective"

    const ques2yes = `It appears that the number of grid points per GPU or node [numGP] is approximately equal to the number of iterations in loop bodies [numIter].  This greatly simplifies GPU-enablement because all the parallelism is exposed in one place.  Congratulations, it appears that your code is GPU-ready. \n i. The next step is to decide on the programming approach to achieve GPU-enablement. Several programming options are available including: \n
    a. Language based approaches using C++ productivity frameworks \n
    b. Writing in an explicitly GPU based language like CUDA, CUDA Fortran or HIP\n
    c. Directive based approaches \n
    ii.The choice of programming language approach is based on your goals \n
    a. Do you want to maintain portability across multiple compute platforms → productivity framework or directive based approach \n
    b. Not concerned about portability → consider explicitly GPU based languages \n
    `

    const ques2no = "Because the total number of grid points per GPU or node [numGP] is significantly larger than the number of iterations [numiter] this looks like some code transformation is necessary.  Unfortunately this is quite common and can take a significant amount of effort to adequately expose the parallelism necessary"


    if (newTotalPoints < 25){
      result += string1
    }

    if(answer == "yes"){
      num += 1
      result += "\n"+ num + ". " + ques1yes
    }else{
      num += 1
      result += "\n"+ num + ". " + ques1no
    }

    if(answer2 == "yes"){
      num += 1
      result += "\n"+ num + ". " + ques1Ayes
    }else{
      num += 1
      result += "\n"+ num + ". " + ques1Bno
    }

    if(answer3 == "yes"){
      num += 1
      result += "\n"+ num + ". " + ques2yes
    }else{
      num += 1
      result += "\n"+ num + ". " +  ques2no
    }

    if (!allOptionsSelected) {
      alert('Please select all options before calculating points.');
    }

    
  };

  const handleCalculatePoints = () => {
    calculatePoints(); // Call calculatePoints without any arguments
  };

  const clearOptions = () => {
    setAnswer('');
    setAnswer2('');
    setAnswer3('');
    setAnswer4('');
    setAnswer5('');
    setPointResult("");
    setResult("")
  };

  const handleClearOptions = () => {
    setShowOptions2(false);
    clearOptions();
  };

  return (
    <div style={{ marginLeft: '20px' }}>
      <ol>
        <li>Does a GPU version of your code already exist?</li>
        <br />
        <label>
          <input
            type="radio"
            name="answer"
            value="yes"
            onClick={handleShowAdditionalQues}
            checked={answer === 'yes'}
            onChange={(e) => setAnswer(e.target.value)}
          />{' '}
          Yes [0 points]
        </label>
        <label>
          <input
            type="radio"
            name="answer"
            value="no"
            onClick={handleShowAdditionalQues}
            checked={answer === 'no'}
            onChange={(e) => setAnswer(e.target.value)}
          />{' '}
          No [4 points]
        </label>
        <br />

        {showOptions2 && (
          <div id="options2">
            <li>Are the desired physics packaged GPU-enabled?</li>
            <br />
            <label>
              <input
                type="radio"
                name="answer2"
                value="yes"
                checked={answer2 === 'yes'}
                onChange={(e) => setAnswer2(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer2"
                value="no"
                checked={answer2 === 'no'}
                onChange={(e) => setAnswer2(e.target.value)}
              />{' '}
              No [3 points]
            </label>
            <br />
          </div>
        )}
        <br />

        <li>Is the code written in such a way that it is GPU-ready?</li>
        <br />
        <ul>
          <li>Is full parallelism available at the loop level?</li>
          <br />
          <ul>
            <label>
              <input
                type="radio"
                name="answer3"
                value="yes"
                checked={answer3 === 'yes'}
                onChange={(e) => setAnswer3(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer3"
                value="no"
                checked={answer3 === 'no'}
                onChange={(e) => setAnswer3(e.target.value)}
              />{' '}
              No [7 points]
            </label>
          </ul>
          <br />
          <li>Does a threaded version of the code exist?</li>
          <br />
          <ul>
            <label>
              <input
                type="radio"
                name="answer4"
                value="yes"
                checked={answer4 === 'yes'}
                onChange={(e) => setAnswer4(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer4"
                value="no"
                checked={answer4 === 'no'}
                onChange={(e) => setAnswer4(e.target.value)}
              />{' '}
              No [7 points]
            </label>
          </ul>
          <br />
          <li>Does the code have some form of verification?</li>
          <br />
          <ul>
            <label>
              <input
                type="radio"
                name="answer5"
                value="yes"
                checked={answer5 === 'yes'}
                onChange={(e) => setAnswer5(e.target.value)}
              />{' '}
              Yes [1 point]
            </label>
            <label>
              <input
                type="radio"
                name="answer5"
                value="no"
                checked={answer5 === 'no'}
                onChange={(e) => setAnswer5(e.target.value)}
              />{' '}
              No [7 points]
            </label>
            <br />
          </ul>
        </ul>
        <br /><br></br>

      </ol>
