import React, { useEffect, useState, useHistory} from 'react';


function Header() {
  const headerStyle = {
    backgroundColor: '#36545d',
    color: 'white',
    padding: '20px',
    textAlign: 'center',
    height: '75px',
  };

  const h1Style = {
    margin: '0',
  };

  const divStyle = {
    marginTop: '5px',
  };

  const history = useHistory();
  const [pageTitle, setPageTitle] = useState('');

  useEffect(() => {
    const unlisten = history.listen((location) => {
      // Update the page title whenever the location changes
      setPageTitle(getPageHeader(location.pathname));
    });

    return () => {
      unlisten();
    };
  }, [history]);

  const getPageHeader = (pathname) => {
    const pageHeaders = {
      '/page1': 'STUDENT EXERCISE 1',
      '/page2': 'STUDENT EXERCISE 2',
      '/page3': 'STUDENT EXERCISE 3',
      // Add more page paths and headers as needed
    };

    return pageHeaders[pathname] || '';
  };

  return (
    <header style={headerStyle}>
      <h1 style={h1Style}>{pageTitle}</h1>
      <div style={divStyle}>[5 minutes]</div>
    </header>
  );
}

export default Header;
